import { Component, OnDestroy, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { CarService } from 'src/app/services/car.service';

@Component({
  selector: 'app-edit-car',
  templateUrl: './edit-car.component.html',
  styleUrls: ['./edit-car.component.css']
})
export class EditCarComponent implements OnInit {
  id:any
  updateCarForm:FormGroup
  constructor(private router:ActivatedRoute,private carService:CarService,private formBuilder:FormBuilder){
    this.router.params.subscribe((params)=>
      this.id=params['id']
    )
     this.updateCarForm=this.formBuilder.group({
      maker:['',Validators.required],
      model:['',Validators.required],
      rentalPrice:['',Validators.required]
    });  
  }
  ngOnInit(): void {
    this.carService.getCarById(this.id).subscribe((res)=>{
      this.updateCarForm.setValue({
        maker:res.maker,
        model:res.model,
        rentalPrice:res.rentalPrice
      })
    },(error)=>{
      console.log(error);
    })
  }
  onUpdate(){

  }
}
<div class="base">
    <div class="login">
      <form [formGroup]="updateCarForm" (submit)="onUpdate()">
        <div class="mb-3">
          <label for="Maker" class="form-label">Maker</label>

          <input
            type="text"
            class="form-control"
            id="Maker"
            placeholder="Enter Maker"
            aria-describedby="maker"
            formControlName="maker"
          />
          <span *ngIf="updateCarForm.controls['maker'].dirty && updateCarForm.hasError('required','maker')" style="color: red;">Maker Field is required</span>
        </div>
        <div class="mb-3">
            <label for="Model" class="form-label">Model</label>

            <input
              type="text"
              class="form-control"
              id="Model"
              placeholder="Enter Maker"
              aria-describedby="Model"
              formControlName="model"
            />
            <span *ngIf="updateCarForm.controls['model'].dirty && updateCarForm.hasError('required','model')" style="color: red;">Model Field is required</span>
          </div>

          <div class="mb-3">
            <label for="RentalPrice" class="form-label">Rental Price</label>

            <input
              type="number"
              class="form-control"
              id="RentalPrice"
              placeholder="Enter Rental Price"
              aria-describedby="rentalPrice"
              formControlName="rentalPrice"
            />
            <span *ngIf="updateCarForm.controls['rentalPrice'].dirty && updateCarForm.hasError('required','rentalPrice')" style="color: red;">Rental Price Field is required</span>
          </div>


        <button type="submit" class="btn btn-primary" [disabled]="updateCarForm.invalid">Submit</button>




      </form>
    </div>
  </div>
