import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { CarService } from 'src/app/services/car.service';
import { UserServiceService } from 'src/app/services/user-service.service';
import { UserStoreService } from 'src/app/services/user-store.service';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css'],
})
export class DashboardComponent {
  none=null
  carDetails:any
  rentalDuration:any
  selectedMaker: any = null;
  selectedModel: any = null;
  selectedPrice: any=null;
  makers: string[] = ['Maruti Suzuki', 'Ford']; // Replace with your maker data
  models: string[] = ['Maruti Suzuki Swift', 'Maruti Suzuki Vitara Brezza', 'Ford Mustang','Ford Escape']; // Replace with your model data


  filterData() {

   const filteredData={maker:null,model:null,rentalPrice:null};
   filteredData.maker=this.selectedMaker;
   filteredData.model=this.selectedModel;
   filteredData.rentalPrice=this.selectedPrice;
   this.carService.searchCar(filteredData).subscribe((res)=>{
    console.log(res);
    this.carDetails=res;
   },(error)=>{
    console.log(error);
   })
  }
  constructor(
    private userService: UserServiceService,
    private userStoreService: UserStoreService,
    private carService:CarService,
    private route:Router
  ) {
    const userPayload = this.userService.decodedToken();
    this.userStoreService.setData(userPayload);
  }
  onSubmit(carId:any){
    console.log(carId);
    console.log(this.rentalDuration);
    this.route.navigate(["rental-agreement",{

    }])
  }
}
