import { Component, OnDestroy, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { CarService } from 'src/app/services/car.service';

@Component({
  selector: 'app-edit-car',
  templateUrl: './edit-car.component.html',
  styleUrls: ['./edit-car.component.css']
})
export class EditCarComponent implements OnInit {
  id:any
  updateCarForm:FormGroup
  constructor(private router:ActivatedRoute,private carService:CarService,private formBuilder:FormBuilder){
    this.router.params.subscribe((params)=>
      this.id=params['id']
    )
     this.updateCarForm=this.formBuilder.group({
      maker:['',Validators.required],
      model:['',Validators.required],
      rentalPrice:['',Validators.required]
    });  
  }
  ngOnInit(): void {
    this.carService.getCarById(this.id).subscribe((res)=>{
      this.updateCarForm.setValue({
        maker:res.maker,
        model:res.model,

      })
    },(error)=>{
      console.log(error);
    })
  }
}
